import './App.css';
import React from 'react'
import ReactDOM from 'react-dom';
import './App.css';

class HomePageCityinfo extends React.Component {
    constructor(props)
    {
        super(props);
        this.state = {city_name : "", city_desc : "", city_temp : "", city_humidity : "", city_wind : "" };
    
    }
    
    static getDerivedStateFromProps(props, state) {
        return {city_name: props.city_name, city_desc : props.city_desc};
    }

    getInfoFromAPI(state){
        let weather = {
            API_Key : "d78f4606c5ba94c8b3d176f95d59163a",
            fetchWeather: function(city_searched) {
              fetch(
                'https://api.openweathermap.org/data/2.5/weather?q='
                +city_searched
                +'&units=metric&appid='
                +this.API_Key
          
              )
              .then((response) => response.json())
              .then((data) => this.displayWeather(data));
            },
            displayWeather: function(data){
              city_name = data;
              city_desc = data.weather[0];
              city_humidity = data.main;
              city_wind = data.wind;
            }
        }
        window.onload = function() {
            weather.fetchWeather("Tallinn");
            weather.fetchWeather("Paris");
            weather.fetchWeather("Berlin");
          };   
    }

    render(){
        return(
            <div class="city_info">
                <h1 class="city_name">{this.state.city_name}</h1>
                <p class = "city_desc">{this.state.city_desc}</p>
                <p class = "city_temp">{this.state.city_temp} Â°C</p>
                <p class = "city_humidity">Humidity : {this.state.city_humidity}%</p>
                <p class = "city_wind">Wind speed : {this.state.city_wind} km/h</p>
            </div>
        );
    }
}

class Testing extends React.Component {
    render() {
      return( 
        <div>
            <HomePageCityinfo city_name = "Tallin"/>
            <HomePageCityinfo city_name = "Paris"/>
            <HomePageCityinfo city_name = "Berlin"/>
        </div>
      );
    }
}

export default Testing;
