import './App.css';
import React from 'react'
import ReactDOM from 'react-dom';
import './App.css';

class HomePageCityinfo extends React.Component {
    constructor(props)
    {
        super(props);
        this.API_Key = "d78f4606c5ba94c8b3d176f95d59163a";
        this.state = {city_name : "", city_desc : "", city_temp : "", city_humidity : "", city_wind : "" };
    
    }
    
    GetInfoAPI(city_searched){
        
        console.log(city_searched);
        fetch(
            'https://api.openweathermap.org/data/2.5/weather?q='
            +city_searched
            +'&units=metric&appid='
            +this.API_Key
        )
        .then((response) => response.json())
        .then((data) => this.DisplayWeather(data));
       
    }
    
    DisplayWeather(data, props, state)
    {
        console.log("DISPLAY")
        this.setState(city_name = data, city_desc = data.weather[0]);
        return {city_name: data, city_desc : data.weather[0]};
    }
    

    static getDerivedStateFromProps(props, state) {
        console.log("getDerived")
        return {city_name: props.city_name, city_desc : props.city_desc};
    }
    

    render(){
        console.log("RENDER")
        return(
            <div className="city_info">
                {this.GetInfoAPI(this.state.city_name)}
                {console.log(this.state.ci)}
                <h1 className="city_name">{this.state.city_name}</h1>
                <p className = "city_desc">{this.state.city_desc}</p>
                <p className = "city_temp">{this.state.city_temp} Â°C</p>
                <p className = "city_humidity">Humidity : {this.state.city_humidity}%</p>
                <p className = "city_wind">Wind speed : {this.state.city_wind} km/h</p>
            </div>
        );
    }
}

