import './App.css';
import React from 'react'
import './App.css';

let weather = {
    API_Key : "d78f4606c5ba94c8b3d176f95d59163a",
    fetchWeather: function(city_searched) {
      fetch(
        'https://api.openweathermap.org/data/2.5/weather?q='
        +city_searched
        +'&units=metric&appid='
        +this.API_Key
  
      )
      .then((response) => response.json())
      .then((data) => this.displayWeather(data));
    },
    displayWeather: function(data){
      const{name} = data;
      const{icon, description} = data.weather[0];
      const{temp, humidity} = data.main;
      const{speed} = data.wind;
      console.log(name,icon,description,temp,humidity,speed);
  
      if(name == "Tallinn" || name == "Paris" || name == "Berlin"){
        //document.querySelector(".icon_"+name).src = "https://openweathermap.org/img/wn/"+icon+"@2x.png";
        document.querySelector(".desc_"+name).innerHTML = description;
        document.querySelector(".temp_"+name).innerHTML = temp+"°C";
        document.querySelector(".humidity_"+name).innerHTML = "Humidity : "+humidity+"%";
        document.querySelector(".wind_"+name).innerHTML = "Wind speed: "+speed+"km/h";
      }
      else{
      }
  
    },
    search : function(){
      localStorage.setItem('city_name', document.querySelector(".InputValue").value);
    },
    
    redirect : function(){
      window.location = "http://127.0.0.1:5501/city_page.html";
    },
  };

class HomePageCityinfo extends React.Component {
    constructor(props)
    {
        super(props);
        this.state = {city_name : "", city_desc : "", city_temp : "", city_humidity : "", city_wind : "" };
    
    }



    
    render(){
        return(
            <div class="city_info">
                <h1 class="city_name">{this.state.city_name}</h1>
                <p class = "city_desc">{this.state.city_name}</p>
                <p class = "city_temp">{this.state.city_name} °C</p>
                <p class = "city_humidity">Humidity : {this.state.city_name}%</p>
                <p class = "city_wind">Wind speed : {this.state.city_name} km/h</p>
            </div>
        );
    }
}
//ReactDOM.render(<HomePageCityinfo favcol="yellow"/>, document.getElementById('root'));


class Testing extends React.Component {
    render() {
      return(
          <div>
        <HomePageCityinfo city_name = "Tallin" />
        <HomePageCityinfo city_name = "Paris"/>
        <HomePageCityinfo city_name = "Berlin"/>
        </div>
      );
    }
}

export default Testing;
