import './App.css';
import React from 'react'
import ReactDOM, { createPortal } from 'react-dom';
import './App.css';

class CityCard extends React.Component {
    constructor(props)
    {
        super(props);
        this.API_Key = "900283061dee29af7cc06c8d4e71abd0";
        this.state = {city_name : "", city_desc : "", city_temp : "", city_humidity : "", city_wind : "" };
    
    }
    
    GetInfoAPI(city_searched){
        
        console.log("APICALL");
        fetch(
            'https://api.openweathermap.org/data/2.5/weather?q='
            +city_searched
            +'&units=metric&appid='
            +this.API_Key
        )
        .then((response) => response.json())
        .then((data) => this.DisplayWeather(data));
       
    }
    
    DisplayWeather(data, props, state)
    {
        this.setState({
            city_desc : data.weather[0], 
            city_temp : 25,
            city_humidity : data,
            city_wind : data.wind});

        //return {city_name: data, city_desc : data.weather[0]};
    }

    blocked(props, state)
    {
        console.log("BLOCKED");

        this.setState({
            city_desc : "pluie", 
            city_temp : (20,80),
            city_humidity : 80,
            city_wind : 11});
        console.log(this.state.city_desc);
        //return {city_name: data, city_desc : data.weather[0]};
    }
    
    static getDerivedStateFromProps(props, state) {
        return {city_name: props.city_name};
    }

    render(){
        console.log("RENDER")
        
        return(
            
            <div className="city_info">
                <h1 className="city_name">{this.state.city_name}</h1>
                <p className = "city_desc">{this.state.city_desc}</p>
                <p className = "city_temp">{this.state.city_temp} Â°C</p>
                <p className = "city_humidity">Humidity : {this.state.city_humidity}%</p>
                <p className = "city_wind">Wind speed : {this.state.city_wind} km/h</p>
            </div>
        );
    }

    componentDidMount()
    {
        setTimeout(() => this.GetInfoAPI(this.state.city_name),5000,this.render());
        //setTimeout(() => this.blocked(),5000,this.render());
    }
}

