import './App.css';
import React from 'react'
import ReactDOM, { createPortal } from 'react-dom';
import './App.css';

class Title extends React.Component{
    render(){
        return(
            <div class="titleBlock"> 
                    Welcome to the Weather App 
                    <select name="units_list" id="units_list">
                        <option value="°C">°C</option>
                        <option value="°F">°F</option>
                    </select>
            </div>
        )
    }
}

class ResearchBar extends React.Component{
    render(){
        return(
            <div class="researchBlock">
                <input type = "text" placeholder="Enter a location" class="InputValue" style="height:25px; width:200px"/> 
                <button><svg stroke="currentColor" fill="currentColor" stroke-width="0" viewBox="0 0 16 16" height="1em" width="1em" xmlns="http://www.w3.org/2000/svg"><path fill-rule="evenodd" d="M10.442 10.442a1 1 0 011.415 0l3.85 3.85a1 1 0 01-1.414 1.415l-3.85-3.85a1 1 0 010-1.415z" clip-rule="evenodd"></path><path fill-rule="evenodd" d="M6.5 12a5.5 5.5 0 100-11 5.5 5.5 0 000 11zM13 6.5a6.5 6.5 0 11-13 0 6.5 6.5 0 0113 0z" clip-rule="evenodd"></path></svg></button>   
            </div>
        )
    }
}

class CityCard extends React.Component {
    constructor(props)
    {
        super(props);
        this.API_Key = "900283061dee29af7cc06c8d4e71abd0";
        this.state = {city_name : "", city_desc : "", city_temp : "", city_humidity : "", city_wind : "" };
    
    }
    
    GetInfoAPI(city_searched){
        
        console.log("APICALL");
        fetch(
            'https://api.openweathermap.org/data/2.5/weather?q='
            +city_searched
            +'&units=metric&appid='
            +this.API_Key
        )
        .then((response) => response.json())
        .then((data) => this.DisplayWeather(data));
       
    }
    
    DisplayWeather(data, props, state)
    {
        this.setState({
            city_desc : data.weather[0], 
            city_temp : 25,
            city_humidity : data,
            city_wind : data.wind});
    }
    /*
    blocked(props, state)
    {
        console.log("BLOCKED");

        this.setState({
            city_desc : "pluie", 
            city_temp : (20,80),
            city_humidity : 80,
            city_wind : 11});
        console.log(this.state.city_desc);
        //return {city_name: data, city_desc : data.weather[0]};
    }
    */
    
    static getDerivedStateFromProps(props, state) {
        return {city_name: props.city_name};
    }

    render(){
        console.log("RENDER")
        
        return(
            
            <div className="city_info">
                <h1 className="city_name">{this.state.city_name}</h1>
                <p className = "city_desc">{this.state.city_desc}</p>
                <p className = "city_temp">{this.state.city_temp} °C</p>
                <p className = "city_humidity">Humidity : {this.state.city_humidity}%</p>
                <p className = "city_wind">Wind speed : {this.state.city_wind} km/h</p>
            </div>
        );
    }

    componentDidMount()
    {
        //setTimeout(() => this.GetInfoAPI(this.state.city_name),5000,this.render());
        //setTimeout(() => this.blocked(),5000,this.render());
    }
}

class Testing extends React.Component {

    render() {
      return( 
        <div>
            <Title />
            <ResearchBar />
            <CityCard city_name = "Tallinn"/>
            <CityCard city_name = "Paris"/>
            <CityCard city_name = "Berlin"/>
        </div>
      );
    }
}

export default Testing;
