import './App.css';
import React from 'react'
import ReactDOM from 'react-dom';
import './App.css';

class HomePageCityinfo extends React.Component {
    constructor(props)
    {
        super(props);
        this.API_Key = "d78f4606c5ba94c8b3d176f95d59163a";
        this.state = {city_name : "", city_desc : "", city_temp : "", city_humidity : "", city_wind : "" };
    
    }
    
    GetInfoAPI(city_searched){
        
        console.log("APICALL");
        fetch(
            'https://api.openweathermap.org/data/2.5/weather?q='
            +city_searched
            +'&units=metric&appid='
            +this.API_Key
        )
        .then((response) => response.json())
        .then((data) => this.DisplayWeather(data));
       
    }
    
    DisplayWeather(data, props, state)
    {
        const{description} = data.weather[0];
        const{temp, humidity} = data.main;
        const{speed} = data.wind;
        this.setState({
            city_desc : description, 
            city_temp : temp,
            city_humidity : humidity,
            city_wind : speed});

        //return {city_name: data, city_desc : data.weather[0]};
    }

    blocked(props, state)
    {
        const{description} = pluie
        const{temp, humidity} = (20,80);
        const{speed} = data.wind;
        this.setState({
            city_desc : description, 
            city_temp : temp,
            city_humidity : humidity,
            city_wind : speed});

        //return {city_name: data, city_desc : data.weather[0]};
    }
    

    static getDerivedStateFromProps(props, state) {
        
        return {city_name: props.city_name};
    }

    render(){
        console.log("RENDER")
        
        return(
            
            <div className="city_info">
                <h1 className="city_name">{this.state.city_name}</h1>
                <p className = "city_desc">{this.state.city_desc}</p>
                <p className = "city_temp">{this.state.city_temp} Â°C</p>
                <p className = "city_humidity">Humidity : {this.state.city_humidity}%</p>
                <p className = "city_wind">Wind speed : {this.state.city_wind} km/h</p>
            </div>
        );
    }

    componentDidMount()
    {
        this.GetInfoAPI(this.state.city_name);
    }
}



class Testing extends React.Component {



    render() {

      return( 
        <div>
            <HomePageCityinfo city_name = "Tallinn"/>
            <HomePageCityinfo city_name = "Paris"/>
            <HomePageCityinfo city_name = "Berlin"/>
        </div>
      );
    }
}

export default Testing;
